contracts/libraries/LinkedAddressList.so.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

library LinkedAddressList {
    address internal constant SENTINEL = address(0x1);
    uint160 internal constant SENTINEL_UINT = 1;

    function page(mapping(address => address) storage target, address start, uint256 pageSize)
        internal
        view
        returns (address[] memory array, address next)
    {
        array = new address[](pageSize);

        uint256 count;
        address current = target[start];
        while (current != address(0x0) && current != SENTINEL && count < pageSize) {
            array[count] = current;
            current = target[current];
            count++;
        }
        next = current;
        assembly {
            mstore(array, count)
        }
    }

    function contains(mapping(address => address) storage target, address item) internal view returns (bool) {
        return SENTINEL != item && target[item] != address(0);
    }

    function setup(mapping(address => address) storage target) internal {
        target[SENTINEL] = SENTINEL;
    }

    function add(mapping(address => address) storage target, address item) internal {
        require(item != address(0) && item != SENTINEL, "item can't be zero or sentinel");
        if (target[item] != address(0)) {
            return;
        }
        target[item] = target[SENTINEL];
        target[SENTINEL] = item;
    }

    function remove(mapping(address => address) storage target, address prevItem, address item) internal {
        require(item != address(0) && item != SENTINEL, "item can't be zero or sentinel");
        require(target[prevItem] == item, "item and preitem mismatch");
        target[prevItem] = target[item];
        delete target[item];
    }
}

contracts/paymaster/VerifyingPaymaster.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@account-abstraction/contracts/core/BasePaymaster.sol";

contract VerifyingPaymaster is BasePaymaster {
    using ECDSA for bytes32;
    using UserOperationLib for UserOperation;

    event SignerChanged(address indexed signer);

    address public signer;

    uint256 private constant VALID_TIMESTAMP_OFFSET = 20;

    uint256 private constant SIGNATURE_OFFSET = 84;

    constructor(IEntryPoint _entryPoint, address _signer) BasePaymaster(_entryPoint) {
        signer = _signer;
        emit SignerChanged(_signer);
    }

    mapping(address => uint256) public senderNonce;

    function pack(UserOperation calldata userOp) internal pure returns (bytes memory ret) {
        bytes calldata pnd = userOp.paymasterAndData;
        assembly {
            let ofs := userOp
            let len := sub(sub(pnd.offset, ofs), 32)
            ret := mload(0x40)
            mstore(0x40, add(ret, add(len, 32)))
            mstore(ret, len)
            calldatacopy(add(ret, 32), ofs, len)
        }
    }

    function getHash(UserOperation calldata userOp, uint48 validUntil, uint48 validAfter)
        public
        view
        returns (bytes32)
    {
        return keccak256(
            abi.encode(
                pack(userOp), block.chainid, address(this), senderNonce[userOp.getSender()], validUntil, validAfter
            )
        );
    }

    function _validatePaymasterUserOp(UserOperation calldata userOp, bytes32, /*userOpHash*/ uint256 requiredPreFund)
        internal
        override
        returns (bytes memory context, uint256 validationData)
    {
        (requiredPreFund);

        (uint48 validUntil, uint48 validAfter, bytes calldata signature) =
            parsePaymasterAndData(userOp.paymasterAndData);
        require(
            signature.length == 64 || signature.length == 65,
            "VerifyingPaymaster: invalid signature length in paymasterAndData"
        );
        bytes32 hash = ECDSA.toEthSignedMessageHash(getHash(userOp, validUntil, validAfter));
        senderNonce[userOp.getSender()]++;

        if (signer != ECDSA.recover(hash, signature)) {
            return ("", _packValidationData(true, validUntil, validAfter));
        }

        return ("", _packValidationData(false, validUntil, validAfter));
    }

    function parsePaymasterAndData(bytes calldata paymasterAndData)
        public
        pure
        returns (uint48 validUntil, uint48 validAfter, bytes calldata signature)
    {
        (validUntil, validAfter) =
            abi.decode(paymasterAndData[VALID_TIMESTAMP_OFFSET:SIGNATURE_OFFSET], (uint48, uint48));
        signature = paymasterAndData[SIGNATURE_OFFSET:];
    }

    function changeSigner(address _signer) external onlyOwner {
        signer = _signer;
        emit SignerChanged(_signer);
    }
}

contracts/SmartAccount.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@account-abstraction/contracts/core/BaseAccount.sol";

import "./core/EntryPointManager.sol";
import "./core/ExecutionManager.sol";
import "./core/FallbackManager.sol";
import "./core/ValidatorManager.sol";
import "./core/RecoveryManager.sol";

contract SmartAccount is
    BaseAccount,
    Initializable,
    EntryPointManager,
    ExecutionManager,
    FallbackManager,
    ValidatorManager,
    RecoveryManager
{
    constructor(IEntryPoint _EntryPoint) EntryPointManager(_EntryPoint) {
        _disableInitializers();
    }

    function initialize(address defalutCallbackHandler, address[] calldata validators, bytes[] calldata data)
        external
        initializer
    {
        if (validators.length != data.length) {
            revert WrongArrayLength();
        }
        _setFallbackHandler(defalutCallbackHandler);
        _setupValidators();
        _setupRecoverors();
        for (uint256 i = 0; i < validators.length;) {
            _enableValidator(validators[i], data[i]);
            unchecked {
                i++;
            }
        }
    }

    function entryPoint() public view virtual override returns (IEntryPoint) {
        return _entryPoint();
    }

    function _validateSignature(UserOperation calldata userOp, bytes32 userOpHash)
        internal
        virtual
        override
        returns (uint256 validationData)
    {
        (address validator, bytes memory signature) = abi.decode(userOp.signature, (address, bytes));

        if (!isValidatorEnabled(validator)) {
            return SIG_VALIDATION_FAILED;
        }
        return IValidator(validator).validateSignature(userOp.sender, userOpHash, signature);
    }

    function recovery(address validator, bytes calldata data) external {
        if (!isRecoverorEnabled(msg.sender)) {
            revert ErrorRecoveror(msg.sender);
        }
        if (!isValidatorEnabled(validator)) {
            revert ErrorValidator(validator);
        }
        IValidator(validator).enable(data);
    }

    function getDeposit() public view returns (uint256) {
        return entryPoint().balanceOf(address(this));
    }

    function addDeposit() public payable {
        entryPoint().depositTo{value: msg.value}(address(this));
    }

    function withdrawDepositTo(address payable withdrawAddress, uint256 amount) public onlySelf {
        entryPoint().withdrawTo(withdrawAddress, amount);
    }
}

contracts/SmartAccountFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import "./handler/DefaultCallbackHandler.sol";
import "./SmartAccount.sol";

contract SmartAccountFactory {
    SmartAccount public immutable accountImplementation;
    address public immutable handler;

    constructor(IEntryPoint _entryPoint, address _handler) {
        handler = _handler;
        accountImplementation = new SmartAccount(_entryPoint);
    }

    function createAccount(address[] calldata validators, bytes[] calldata data, uint256 salt)
        public
        returns (SmartAccount ret)
    {
        address addr = getAddress(validators, data, salt);
        uint256 codeSize = addr.code.length;
        if (codeSize > 0) {
            return SmartAccount(payable(addr));
        }
        ret = SmartAccount(
            payable(
                new ERC1967Proxy{salt : bytes32(salt)}(
                address(accountImplementation),
                abi.encodeCall(SmartAccount.initialize, (handler, validators, data))
                )
            )
        );
    }

    function getAddress(address[] calldata validators, bytes[] calldata data, uint256 salt)
        public
        view
        returns (address)
    {
        return Create2.computeAddress(
            bytes32(salt),
            keccak256(
                abi.encodePacked(
                    type(ERC1967Proxy).creationCode,
                    abi.encode(
                        address(accountImplementation),
                        abi.encodeCall(SmartAccount.initialize, (handler, validators, data))
                    )
                )
            )
        );
    }
}

